package com.dev.ForecastApiTestJarV2.contract;

import java.math.BigInteger;
import java.util.Arrays;
import java.util.Collections;

import org.springframework.beans.factory.annotation.Value;
import org.springframework.stereotype.Component;
import org.web3j.abi.TypeReference;
import org.web3j.abi.datatypes.Type;
import org.web3j.abi.datatypes.Utf8String;
import org.web3j.abi.datatypes.generated.Uint256;
import org.web3j.crypto.Credentials;
import org.web3j.protocol.Web3j;
import org.web3j.protocol.core.RemoteFunctionCall;
import org.web3j.protocol.core.methods.response.TransactionReceipt;
import org.web3j.tx.Contract;
import org.web3j.tx.gas.ContractGasProvider;

public class ContractWrapperV2 extends Contract {

	public static final String BINARY = "";

	public static final String FUNC_APPROVE = "approve";

	public static final String FUNC_BALANCEOF = "balanceOf";

	public static final String FUNC_GETAPPROVED = "getApproved";

	public static final String FUNC_ISAPPROVEDFORALL = "isApprovedForAll";

	public static final String FUNC_NAME = "name";

	public static final String FUNC_OWNEROF = "ownerOf";

	public static final String FUNC_safeTransferFrom = "safeTransferFrom";

	public static final String FUNC_SETAPPROVALFORALL = "setApprovalForAll";

	public static final String FUNC_SUPPORTSINTERFACE = "supportsInterface";

	public static final String FUNC_SYMBOL = "symbol";

	public static final String FUNC_TRANSFERFROM = "transferFrom";

	public static final String FUNC_TOKENURI = "tokenURI";

	public static final String FUNC_CREATE = "create";

	@Value("${infura.API_URL}")
    private String INFURA_API_URL;

    @Value("${metamask.PRIVATE_KEY}")
    private String PRIVATE_KEY;

    @Value("${metamask.CONTRACT_ADDRESS}")
    private String CONTRACT_ADDRESS;
    
    @Value("${metamask.WALLET_ADDRESS}")
    private String WALLET_ADDRESS;
	
	public ContractWrapperV2(
			String contractAddress, 
			Web3j web3j, 
			Credentials credentials,
			ContractGasProvider contractGasProvider) {
		
		super(BINARY, contractAddress, web3j, credentials, contractGasProvider);
		// TODO Auto-generated constructor stub
	}

	public static ContractWrapperV2 load(String contractAddress, Web3j web3j, Credentials credentials,
			ContractGasProvider contractGasProvider) {

		return new ContractWrapperV2(contractAddress, web3j, credentials, contractGasProvider);

	}

	public RemoteFunctionCall<String> name() {
		final org.web3j.abi.datatypes.Function function = new org.web3j.abi.datatypes.Function(FUNC_NAME,
				Arrays.<Type>asList(), Arrays.<TypeReference<?>>asList(new TypeReference<Utf8String>() {
				}));
		return executeRemoteCallSingleValueReturn(function, String.class);
	}

	public RemoteFunctionCall<BigInteger> balanceOf(String owner) {
		final org.web3j.abi.datatypes.Function function = new org.web3j.abi.datatypes.Function(FUNC_BALANCEOF,
				Arrays.<Type>asList(new org.web3j.abi.datatypes.Address(owner)),
				Arrays.<TypeReference<?>>asList(new TypeReference<Uint256>() {
				}));
		return executeRemoteCallSingleValueReturn(function, BigInteger.class);
	}

	public RemoteFunctionCall<TransactionReceipt> create(String to, String tokenURI) {
		final org.web3j.abi.datatypes.Function function = new org.web3j.abi.datatypes.Function(FUNC_CREATE,
				Arrays.<Type>asList(new org.web3j.abi.datatypes.Address(to),
						new org.web3j.abi.datatypes.Utf8String(tokenURI)),
				Collections.<TypeReference<?>>emptyList());
		return executeRemoteCallTransaction(function);
	}

}
